import { useContext, useState } from "react";
import { Link } from "react-router";
import { UserContext } from "./UserContext";
import { useEffect } from "react";

const Page = () => {
    const products = [
        {
            id: 1,
            name: "Wireless Headphones",
            price: 59.99,
            category: "Electronics",
            image: "data:image/jpeg;base64,",
        },
        {
            id: 2,
            name: "Smartphone Case",
            price: 12.99,
            category: "Accessories",
            image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnQcIvWb1KbbiFlpE1PHF04_JdiIyDGLLHvQ&s",
        },
        {
            id: 3,
            name: "Bluetooth Speaker",
            price: 34.99,
            category: "Electronics",
            image: "data:image/jpeg;base64,",
        },
        {
            id: 4,
            name: "Running Shoes",
            price: 89.99,
            category: "Footwear",
            image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQZbnXUNTM5pyGs5BvTpqrVRZtbbbawTRpUjQ&s",
        },
        {
            id: 5,
            name: "Backpack",
            price: 45.99,
            category: "Bags",
            image: "data:image/jpeg;base64,",
        },
        {
            id: 6,
            name: "Gaming Mouse",
            price: 29.99,
            category: "Gaming",
            image: "data:image/jpeg;base64,",
        },
        {
            id: 7,
            name: "Smartwatch",
            price: 99.99,
            category: "Wearables",
            image: "data:image/jpeg;base64,",
        },
        {
            id: 8,
            name: "Sunglasses",
            price: 19.99,
            category: "Fashion",
            image: "data:image/png;base64,",
        },
        {
            id: 9,
            name: "Mechanical Keyboard",
            price: 74.99,
            category: "Accessories",
            image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR81C65R8qml4DW-oRfMh409HLU0ZFR4B28dA&s",
        },
        {
            id: 10,
            name: "Portable Charger",
            price: 24.99,
            category: "Electronics",
            image: "data:image/jpeg;base64,",
        }
    ];

    const [search, setSearch] = useState("")
    const [category, setCategory] = useState('');
    const [data, setData] = useState(products);

    const [count, setCount] = useState(0);

    const { cart, setCart } = useContext(UserContext)


    function hnadlesearchCategory(event) {
        const val = event.target.value;
        setSearch(val);
        const ele = products.filter((ele) => (ele.name.toLowerCase()).startsWith(val.toLowerCase()))
        setData(ele);
    }

    function handlesetCategory(event) {
        const val = event.target.value;
        setCategory(val);
        if (val === "ALL") {
            setData(products)
            return;
        }
        const ele = products.filter((ele) => (ele.category === val))
        setData(ele);
    }

    // using Context ===========

    function handleAddtoCart(index) {
        const newpro = data.find((ele, ind) => ind === index);
        setCart([...cart, newpro])
    }

    useEffect(() => {
        setCount(cart.length);
    }, [cart])

    return (
        <div className="p-2">
            <div className="flex justify-between">
                <div>
                    <input type="text" value={search} placeholder="search product" className="border p-1" onChange={hnadlesearchCategory} />
                    <select name="product" id="category" value={category} className="border p-1" onChange={handlesetCategory}>
                        <option value="ALL">Select Category</option>
                        <option value="Electronics">Electronics</option>
                        <option value="Accessories">Accessories</option>
                        <option value="Footwear">Footwear</option>
                        <option value="Bags">Bags</option>
                        <option value="Gaming">Gaming</option>
                        <option value="Wearables">Wearables</option>
                        <option value="Fashion">Fashion</option>
                    </select>
                </div>
                <div>
                    <button className="bg-yellow-400 rounded-xl p-2"><Link to="/cart">Cart</Link></button>
                    <button className="bg-black text-white p-1 rounded">{count}</button>
                </div>
            </div>

            <div className="flex flex-wrap items-start gap-20 p-10">
                {
                    data.map((ele, index) => {
                        return (
                            <div key={ele.id} className="border">
                                <img src={ele.image} alt="" className="w-[270px] h-[200px]" />
                                <div className="text-center p-2">
                                    <div>{ele.name}</div>
                                    <div>{ele.price}</div>
                                    <div>
                                        <button onClick={() => handleAddtoCart(index)} className="bg-black text-white p-1 px-3 rounded-xl">add to Cart</button>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default Page